SET CLIENT_ENCODING TO 'SJIS';

/*
	[サブ機能  ] EX.基本マスタ
	[機能ＩＤ  ] EX02
	[機能名称  ] 分類マスタ
	[リビジョン] 1
	[関数名    ] p_Example
	[作 成 日  ] 2021/08/17
	[作 成 者  ] UNICCS/y-mano
	[更新履歴  ] 
*/

CREATE OR REPLACE FUNCTION PXC_Example_001.p_Example (
	i_Example	IN	VARCHAR
) RETURNS REFCURSOR AS $$

/*

$START
$FUNCTION\1
	分類マスタ（第２分類）を検索します。
$PAR\1
	第２分類コード
$RETURN\1
	結果セット
$END
*/
DECLARE
	ls_SqlErrMsg	UMF_RESOURCEDEF.RESOURCEVAL%TYPE := '';
	ls_SqlState		VARCHAR(5);

BEGIN


EXCEPTION
	WHEN OTHERS THEN
		SELECT  SQLSTATE	INTO ls_SqlState;
		SELECT  SQLERRM		INTO ls_SqlErrMsg;
		PERFORM PXS_STANDARD.p_NoticeError(
			'PXC_Example_001.p_Example.SYSTEMERROR',
			ls_SqlState,
			ls_SqlErrMsg
		);
END;
$$ LANGUAGE 'plpgsql';

RESET CLIENT_ENCODING;

EXCEPTION
	WHEN RAISE_EXCEPTION THEN
		SELECT	SQLERRM	INTO ls_SqlErrMsg;
		PERFORM PXS_STANDARD.p_NoticeErrorExpansion(
			'ERROR',
			'',
			ls_SqlErrMsg
		);
	WHEN OTHERS THEN
		SELECT	SQLSTATE	INTO ls_SqlState;
		SELECT	SQLERRM		INTO ls_SqlErrMsg;
		PERFORM PXS_STANDARD.p_NoticeError(
			'PXC_EXAMPLE_001.p_Example.SYSTEMERROR',
			ls_SqlState,
			ls_SqlErrMsg
		);

ls_ExceptionMessage := PXS_STANDARD.f_GetResourceVal(
	'PXC_EXAMPLE_001.p_EXAMPLE.NAME_RESOURCE',
	i_Params1,
	i_Params2
);
RAISE '%', ls_ExceptionMessage USING ERRCODE = 'raise_exception';

OPEN lcur_Example;
FETCH lcur_Example INTO lrec_Example;
	-- TO DO

CLOSE lcur_Example;

FOR lrec_Example IN lcur_Example LOOP
	-- TO DO LIST
END LOOP;

OPEN returnCursor FOR
	SELECT	COLUMN1_EXAMPLE
	FROM	UMF_CLASS4 CLASS
	WHERE	CONDITION_1	= CONDITION_OTHER
	AND		CONDITION_1	= CONDITION_OTHER;

RETURN returnCursor;

CASE (lrec_CSVUploadColumnData.COLUMN7)
	WHEN ('1') THEN
		ls_DenpKind	:= '10';
		ls_DenpType	:= lrec_OrderHeader.DENPTYPE;

	WHEN ('2') THEN
		ls_DenpKind	:= '12';
		ls_DenpType	:= lrec_ReturnHeader.DENPTYPE;

	ELSE
		ls_DenpKind	:= '10';
		ls_DenpType	:= lrec_OrderHeader.DENPTYPE;
END;

WHERE	EXISTS (	SELECT	1
					FROM	TMP_TABLE_NAME COLUMNDATA
					WHERE	COLUMNDATA.COLUMN1			= PPN.COLUMN_1
					AND		COLUMNDATA.VALIDFLAG		= '0'
		);

WHERE	EXISTS (	SELECT	1
					FROM	TMP_TABLE_NAME COLUMNDATA
					WHERE	COLUMNDATA.COLUMN1			= PPN.COLUMN_1
					AND		COLUMNDATA.VALIDFLAG		= '0'
		);
